mapper.update:
update T_RESUME
set RS_EDUCATION=#{rsEducation}, RS_MAJOR=#{rsMajor}, RS_EXPERIENCE=#{rsExperience},
 RS_SKILLS=#{rsSkills}, RS_AWARD=#{rsAward}, RS_INTRODUCTION=#{rsIntroduction}
where RS_ID=#{rsId}

mapper.select:
select * from T_RESUME
left outer join T_BASIC on BS_RSID=RS_ID


        RS_ID=#{rsId}


        and RS_VIID=#{rsViId}
